const { Stack, CfnOutput } = require('aws-cdk-lib');
const ec2 = require('aws-cdk-lib/aws-ec2');

class NetworkingStack extends Stack {
  constructor(scope, id, props) {
    super(scope, id, props);

    // Create a VPC
    const vpc = new ec2.Vpc(this, 'TodoAppVpc', {
      maxAzs: 2,
      natGateways: 1
    });

    // Create a security group for the ECS service
    const serviceSecurityGroup = new ec2.SecurityGroup(this, 'TodoAppServiceSG', {
      vpc,
      allowAllOutbound: true,
      description: 'Security group for Todo App ECS service'
    });

    // Create a security group for the load balancer
    const lbSecurityGroup = new ec2.SecurityGroup(this, 'TodoAppLBSG', {
      vpc,
      allowAllOutbound: true,
      description: 'Security group for Todo App load balancer'
    });

    // Allow inbound traffic on port 80 to the load balancer
    lbSecurityGroup.addIngressRule(
      ec2.Peer.anyIpv4(),
      ec2.Port.tcp(80),
      'Allow HTTP traffic from anywhere'
    );

    // Allow traffic from the load balancer to the service
    serviceSecurityGroup.addIngressRule(
      lbSecurityGroup,
      ec2.Port.tcp(3000),
      'Allow traffic from the load balancer to the service'
    );

    // Outputs
    this.vpc = vpc;
    this.serviceSecurityGroup = serviceSecurityGroup;
    this.lbSecurityGroup = lbSecurityGroup;

    // CloudFormation outputs
    new CfnOutput(this, 'VpcId', {
      value: vpc.vpcId,
      description: 'The ID of the VPC',
      exportName: 'TodoAppVpcId'
    });

    new CfnOutput(this, 'ServiceSecurityGroupId', {
      value: serviceSecurityGroup.securityGroupId,
      description: 'The ID of the service security group',
      exportName: 'TodoAppServiceSecurityGroupId'
    });

    new CfnOutput(this, 'LBSecurityGroupId', {
      value: lbSecurityGroup.securityGroupId,
      description: 'The ID of the load balancer security group',
      exportName: 'TodoAppLBSecurityGroupId'
    });
  }
}

module.exports = { NetworkingStack };

