const logs = require('aws-cdk-lib/aws-logs');
const ecr_assets = require('aws-cdk-lib/aws-ecr-assets');
const path = require('path');

class EcsStack extends Stack {
  constructor(scope, id, props) {
    super(scope, id, props);

    // Import networking resources from the networking stack
    const vpc = ec2.Vpc.fromVpcAttributes(this, 'ImportedVpc', {
      vpcId: props.vpcId,
      availabilityZones: props.availabilityZones,
      publicSubnetIds: props.publicSubnetIds,
      privateSubnetIds: props.privateSubnetIds
    });

    const serviceSecurityGroup = ec2.SecurityGroup.fromSecurityGroupId(
      this,
      'ImportedServiceSG',
      props.serviceSecurityGroupId
    );

    const lbSecurityGroup = ec2.SecurityGroup.fromSecurityGroupId(
      this,
      'ImportedLBSG',
      props.lbSecurityGroupId
    );

    // Build Docker image directly from CDK
    const dockerImageAsset = new ecr_assets.DockerImageAsset(this, 'TodoAppImage', {
      directory: path.join(__dirname, '..', '..'), // Path to your Dockerfile directory (parent of cdk folder)
    });

    // Create an ECS cluster
    const cluster = new ecs.Cluster(this, 'TodoAppCluster', {
      vpc,
      clusterName: 'todo-app-cluster'
    });

    // Create a task execution role
    const executionRole = new iam.Role(this, 'TodoAppTaskExecutionRole', {
      assumedBy: new iam.ServicePrincipal('ecs-tasks.amazonaws.com'),
      managedPolicies: [
        iam.ManagedPolicy.fromAwsManagedPolicyName('service-role/AmazonECSTaskExecutionRolePolicy')
      ]
    });

    // Create a task role
    const taskRole = new iam.Role(this, 'TodoAppTaskRole', {
      assumedBy: new iam.ServicePrincipal('ecs-tasks.amazonaws.com')
    });

    // Create a log group
    const logGroup = new logs.LogGroup(this, 'TodoAppLogGroup', {
      logGroupName: '/ecs/todo-app',
      removalPolicy: RemovalPolicy.DESTROY // NOT recommended for production
    });

    // Create a task definition
    const taskDefinition = new ecs.FargateTaskDefinition(this, 'TodoAppTaskDef', {
      memoryLimitMiB: 1024,
      cpu: 512,
      executionRole,
      taskRole
    });

    // Add container to task definition using the built image
    const container = taskDefinition.addContainer('TodoAppContainer', {
      // Use the Docker image asset - this will automatically build and push the image
      image: ecs.ContainerImage.fromDockerImageAsset(dockerImageAsset),
      logging: ecs.LogDrivers.awsLogs({
        streamPrefix: 'todo-app',
        logGroup
      }),
      environment: {
        NODE_ENV: 'production'
      },
      portMappings: [{ containerPort: 3000 }],
      healthCheck: {
        command: ["CMD-SHELL", "curl -f http://localhost:3000/health || exit 1"],
        interval: Duration.seconds(30),
        timeout: Duration.seconds(5),
        retries: 5,
        startPeriod: Duration.seconds(120)
      }
    });

    // Create a Fargate service
    const service = new ecs.FargateService(this, 'TodoAppService', {
      cluster,
      taskDefinition,
      desiredCount: 2,
      assignPublicIp: true, // Enable public IP assignment for tasks in private subnets
      securityGroups: [serviceSecurityGroup],
      healthCheckGracePeriodSeconds: 120
    });

    // Create an Application Load Balancer
    const lb = new elbv2.ApplicationLoadBalancer(this, 'TodoAppLB', {
      vpc,
      internetFacing: true,
      loadBalancerName: 'todo-app-lb',
      securityGroup: lbSecurityGroup
    });

    // Add a listener
    const listener = lb.addListener('TodoAppListener', {
      port: 80
    });

    // Add target group to the listener
    listener.addTargets('TodoAppTargetGroup', {
      port: 80,
      targets: [service],
      healthCheck: {
        path: '/health',
        interval: Duration.seconds(60),
        timeout: Duration.seconds(5),
        healthyThresholdCount: 2,
        unhealthyThresholdCount: 3
      }
    });

    // Output the load balancer DNS name
    this.loadBalancerDns = lb.loadBalancerDnsName;

    // Add CloudFormation output for the load balancer DNS
    new CfnOutput(this, 'LoadBalancerDNS', {
      value: lb.loadBalancerDnsName,
      description: 'The DNS name of the load balancer'
    });

    // Add CloudFormation output for the Docker image URI
    new CfnOutput(this, 'DockerImageURI', {
      value: dockerImageAsset.imageUri,
      description: 'The URI of the Docker image'
    });
  }
}

module.exports = { EcsStack };

