#!/usr/bin/env node

const cdk = require('aws-cdk-lib');
const { NetworkingStack } = require('../lib/networking-stack');
const { EcsStack } = require('../lib/ecs-stack');

const app = new cdk.App();

// Deploy the networking stack first
const networkingStack = new NetworkingStack(app, 'TodoAppNetworkingStack', {
  env: {
    account: process.env.CDK_DEFAULT_ACCOUNT,
    region: process.env.CDK_DEFAULT_REGION || 'us-west-2'
  },
  description: 'Networking infrastructure for Todo App (VPC, subnets, security groups)'
});

// Get the VPC and subnet information
const vpc = networkingStack.vpc;
const publicSubnets = vpc.publicSubnets;
const privateSubnets = vpc.privateSubnets;

// Deploy the ECS stack which depends on the networking stack
const ecsStack = new EcsStack(app, 'TodoAppEcsStack', {
  env: {
    account: process.env.CDK_DEFAULT_ACCOUNT,
    region: process.env.CDK_DEFAULT_REGION || 'us-west-2'
  },
  description: 'ECS infrastructure for Todo App (cluster, service, load balancer)',
  // Pass the networking resources to the ECS stack
  vpcId: vpc.vpcId,
  availabilityZones: vpc.availabilityZones,
  publicSubnetIds: publicSubnets.map(subnet => subnet.subnetId),
  privateSubnetIds: privateSubnets.map(subnet => subnet.subnetId),
  serviceSecurityGroupId: networkingStack.serviceSecurityGroup.securityGroupId,
  lbSecurityGroupId: networkingStack.lbSecurityGroup.securityGroupId
});

// Add dependency to ensure the networking stack is created first
ecsStack.addDependency(networkingStack);

app.synth();

